mapper.select:
select * from ( select a.*, rownum rnum from( select C.CHAL_NO ,C.USER_NO ,C.CATEGORY_NO ,C.TITLE ,C.CONTENT ,C.THUMBNAIL ,C.VERIFY_CYCLE ,C.CREATE_DATE ,C.START_DATE ,C.END_DATE ,C.STATUS ,C.PICTURE_REQUIRED ,C.REPLY_REQUIRED ,CC.CATEGORY_NAME ,U.NICK ,COUNT(cp.user_no) as PARTICIPATE_COUNT ,ROUND(SUM(CASE WHEN cp.STATUS = 'S' THEN 1 ELSE 0 END) * 100.0 / COUNT(cp.user_no),2) as SUCCESS_RATIO
from CHALLENGE c left join CHALLENGE_CATEGORY cc on(c.CATEGORY_NO = cc.CATEGORY_NO) left join CHALLENGE_PARTICIPATION cp on(c.CHAL_NO = cp.CHAL_NO) left join USER u on(c.USER_NO = u.USER_NO)
where 1=1
and C.TITLE = #{search}
and C.CONTENT = #{search}
and U.NICK = #{search}
and (C.TITLE = #{search} or C.CONTENT = #{search} or U.NICK = #{search})
and C.CATEGORY_NO = #{categoryNo}
and C.CATEGORY_NO >= #{categoryNo} and C.CATEGORY_NO < (#{categoryNo} + 10)
and (C.VERIFY_CYCLE & #{verifyCycle}) = #{verifyCycle}
and C.VERIFY_CYCLE = #{verifyCycle}
and C.START_DATE > #{startDate}
and C.END_DATE < #{endDate}
and C.STATUS = #{status}
and C.PICTURE_REQUIRED = #{pictureRequired}
and C.REPLY_REQUIRED = #{replyRequired}
group by C.CHAL_NO ,C.USER_NO ,C.CATEGORY_NO ,C.TITLE ,C.CONTENT ,C.THUMBNAIL ,C.VERIFY_CYCLE ,C.CREATE_DATE ,C.START_DATE ,C.END_DATE ,C.STATUS ,C.PICTURE_REQUIRED ,C.REPLY_REQUIRED ,CC.CATEGORY_NAME ,U.NICK
order by
COUNT(cp.user_no) desc
ROUND(SUM(CASE WHEN cp.STATUS = 'S' THEN 1 ELSE 0 END) * 100.0 / COUNT(cp.user_no),2) desc
C.CREATE_DATE desc
) a where rnum <= (#{currentPage}+1) * #{showLimit} ) where rnum > #{currentPage} * #{showLimit}

mapper.insert:

	SELECT SEQ_CHALLENGE.nextval FROM dual

INSERT INTO CHALLENGE(
	CHAL_NO
	,USER_NO
	,CATEGORY_NO
	,TITLE
	,CONTENT
	,THUMBNAIL
	,VERIFY_CYCLE
	,CREATE_DATE
	,START_DATE
	,END_DATE
	,STATUS
	,PICTURE_REQUIRED
	,REPLY_REQUIRED
	)
VALUES (
	#{chalNo}
	,#{userNo}
	,#{categoryNo}
	,#{title}
	,#{content}
	,#{thumbnail}
	,#{verifyCycle}
	,SYSDATE


			,SYSDATE


			,#{startDate}


	,#{endDate}
	,#{status}
	,#{pictureRequired}
	,#{replyRequired}
	)
