mapper.select:
select * from ( select a1.* ,P.PICTURE ,rownum rnum ,(SELECT THUMBNAIL FROM CHALLENGE C2 WHERE C2.CHAL_NO = a1.CHAL_NO) AS THUMBNAIL from( select C.CHAL_NO ,C.USER_NO ,C.TITLE ,C.CONTENT ,C.VERIFY_CYCLE ,C.CREATE_DATE ,C.START_DATE ,C.END_DATE ,C.STATUS ,C.PICTURE_REQUIRED ,C.REPLY_REQUIRED ,CC.CATEGORY_NAME ,U.NICK ,P.IS_OPEN ,COUNT(cp.user_no) as PARTICIPATE_COUNT ,CASE WHEN COUNT(cp.user_no) = 0 THEN 0 ELSE ROUND( SUM(CASE WHEN cp.STATUS = 'S' THEN 1 ELSE 0 END) * 100.0 / COUNT(cp.user_no) , 2) END AS SUCCESS_RATIO ,CASE WHEN COUNT(cp.user_no) = 0 THEN 0 ELSE ROUND( SUM(CASE WHEN cp.STATUS = 'F' THEN 1 ELSE 0 END) * 100.0 / COUNT(cp.user_no) , 2) END AS FAIL_RATIO
from CHALLENGE c left join CHALLENGE_CATEGORY cc on(c.CATEGORY_NO = cc.CATEGORY_NO) left join CHALLENGE_PARTICIPATION cp on(c.CHAL_NO = cp.CHAL_NO) left join USERS u on(c.USER_NO = u.USER_NO) left join PROFILE p on(u.USER_NO = p.USER_NO)
where C.STATUS != 'D'
and C.TITLE like #{searchPattern}
and C.CONTENT like #{searchPattern}
and U.NICK like #{searchPattern}
and ( C.TITLE like #{searchPattern} or C.CONTENT like #{searchPattern} or U.NICK like #{searchPattern} )
and C.CATEGORY_NO = #{categoryNo}
and C.CATEGORY_NO >= #{categoryNo} and C.CATEGORY_NO < (#{categoryNo} + 10)
and BITAND(C.VERIFY_CYCLE, #{verifyCycle}) = #{verifyCycle}
and C.VERIFY_CYCLE = #{verifyCycle}
and C.START_DATE > #{startDate1}
and C.START_DATE < #{startDate2}
and C.END_DATE > #{endDate1}
and C.END_DATE < #{endDate2}
and C.STATUS = #{status}
and C.PICTURE_REQUIRED = #{pictureRequired}
and C.REPLY_REQUIRED = #{replyRequired}
group by C.CHAL_NO ,C.USER_NO ,C.TITLE ,C.CONTENT ,C.VERIFY_CYCLE ,C.CREATE_DATE ,C.START_DATE ,C.END_DATE ,C.STATUS ,C.PICTURE_REQUIRED ,C.REPLY_REQUIRED ,CC.CATEGORY_NAME ,U.NICK ,P.IS_OPEN
order by
COUNT(cp.user_no) desc
CASE WHEN SUM(CASE WHEN cp.STATUS = 'S' THEN 1 ELSE 0 END) = 0 THEN 0 ELSE ROUND(SUM(CASE WHEN cp.STATUS = 'S' THEN 1 ELSE 0 END) * 100.0 / COUNT(cp.user_no), 2) END DESC
CASE WHEN C.STATUS = 'Y' THEN 0 ELSE 1 END, C.CREATE_DATE DESC
) a1
left join PROFILE P ON a1.USER_NO = P.USER_NO
where rownum <= (#{currentPage}+1) * #{showLimit} ) where rnum > #{currentPage} * #{showLimit}

mapper.insert:

	SELECT SEQ_CHALLENGE.nextval FROM dual

INSERT INTO CHALLENGE(
	CHAL_NO
	,USER_NO
	,CATEGORY_NO
	,TITLE
	,CONTENT
	,THUMBNAIL
	,VERIFY_CYCLE
	,CREATE_DATE
	,START_DATE
	,END_DATE
	,STATUS
	,PICTURE_REQUIRED
	,REPLY_REQUIRED
	)
VALUES (
	#{chalNo}
	,#{userNo}
	,#{categoryNo}
	,#{title}
	,#{content}
	,#{thumbnail}
	,#{verifyCycle}
	,SYSDATE


			,SYSDATE


			,#{startDate}


	,#{endDate}
	,'Y'
	,#{pictureRequired}
	,#{replyRequired}
	)

mapper.select:
select * from ( select a1.* ,P.PICTURE ,rownum rnum ,(SELECT THUMBNAIL FROM CHALLENGE C2 WHERE C2.CHAL_NO = a1.CHAL_NO) AS THUMBNAIL from( select C.CHAL_NO ,C.USER_NO ,C.TITLE ,C.CONTENT ,C.VERIFY_CYCLE ,C.CREATE_DATE ,C.START_DATE ,C.END_DATE ,C.STATUS ,C.PICTURE_REQUIRED ,C.REPLY_REQUIRED ,CC.CATEGORY_NAME ,U.NICK ,P.IS_OPEN ,COUNT(cp.user_no) as PARTICIPATE_COUNT ,CASE WHEN COUNT(cp.user_no) = 0 THEN 0 ELSE ROUND( SUM(CASE WHEN cp.STATUS = 'S' THEN 1 ELSE 0 END) * 100.0 / COUNT(cp.user_no) , 2) END AS SUCCESS_RATIO ,CASE WHEN COUNT(cp.user_no) = 0 THEN 0 ELSE ROUND( SUM(CASE WHEN cp.STATUS = 'F' THEN 1 ELSE 0 END) * 100.0 / COUNT(cp.user_no) , 2) END AS FAIL_RATIO
from CHALLENGE c left join CHALLENGE_CATEGORY cc on(c.CATEGORY_NO = cc.CATEGORY_NO) left join CHALLENGE_PARTICIPATION cp on(c.CHAL_NO = cp.CHAL_NO) left join USERS u on(c.USER_NO = u.USER_NO) left join PROFILE p on(u.USER_NO = p.USER_NO)
where C.STATUS != 'D'
and c.USER_NO = #{userNo}
and cp.USER_NO = #{userNo} and c.USER_NO != #{userNo}
GROUP BY C.CHAL_NO ,C.USER_NO ,C.TITLE ,C.CONTENT ,C.VERIFY_CYCLE ,C.CREATE_DATE ,C.START_DATE ,C.END_DATE ,C.STATUS ,C.PICTURE_REQUIRED ,C.REPLY_REQUIRED ,CC.CATEGORY_NAME ,U.NICK ,P.IS_OPEN
order by CASE WHEN C.STATUS = 'Y' THEN 0 ELSE 1 END, C.CREATE_DATE DESC ) a1 left join PROFILE P ON a1.USER_NO = P.USER_NO where rownum <= (#{currentPage}+1) * #{showLimit} ) where rnum > #{currentPage} * #{showLimit}

select PICTURE_REQUIRED, REPLY_REQUIRED
from CHALLENGE
where CHAL_NO = #{chalNo}

select count(*)
from CHALLENGE
where CHAL_NO = #{chalNo}
and USER_NO = #{userNo}

mapper.update:
update CHALLENGE
set CATEGORY_NO = #{categoryNo}
	,TITLE = #{title}
	,CONTENT = #{content}
	,THUMBNAIL = #{thumbnail}
	,VERIFY_CYCLE = #{verifyCycle}


			,START_DATE = SYSDATE


			,START_DATE = #{startDate}


	,END_DATE = #{endDate}
	,PICTURE_REQUIRED = #{pictureRequired}
	,REPLY_REQUIRED = #{replyRequired}
where CHAL_NO = #{chalNo}
and USER_NO = #{userNo}
