mapper.insert:

	SELECT SEQ_CHALLENGE.nextval FROM dual

INSERT INTO CHALLENGE(
	CHAL_NO
	,USER_NO
	,CATEGORY_NO
	,TITLE
	,CONTENT
	,THUMBNAIL
	,VERIFY_CYCLE
	,CREATE_DATE
	,START_DATE
	,END_DATE
	,STATUS
	,PICTURE_REQUIRED
	,REPLY_REQUIRED
	)
VALUES (
	#{chalNo}
	,#{userNo}
	,#{categoryNo}
	,#{title}
	,#{content}
	,#{thumbnail}
	,#{verifyCycle}
	,SYSDATE
	,#{startDate}
	,#{endDate}
	,#{status}
	,#{pictureRequired}
	,#{replyRequired}
	)
