mapper.select:
select *
from(
    select a1.*, rownum rnum
    from(
        select cc.COMMENT_NO
              ,cc.USER_NO
              ,cc.CHAL_NO
              ,cc.RECOMMENT_TARGET
              ,cc.REPLY
              ,cc.CREATE_DATE
              ,cc.STATUS
              ,u.NICK
              ,p.PICTURE
              ,p.IS_OPEN
        from CHALLENGE_COMMENT cc
        left join USERS u on(u.USER_NO = cc.USER_NO)
        left join PROFILE p on(cc.USER_NO = p.USER_NO)
        where cc.CHAL_NO = #{chalNo}
        and cc.RECOMMENT_TARGET = #{recommentTarget}
        order by cc.CREATE_DATE
    ) a1
    where rownum <= (#{currentRecommentPage}+1) * #{showLimit}
)
where rnum > #{currentRecommentPage} * #{showLimit}

mapper.insert:
insert into CHALLENGE_COMMENT(
	COMMENT_NO
	,USER_NO
	,CHAL_NO
	,RECOMMENT_TARGET
	,REPLY
	,CREATE_DATE
	,STATUS
	)
values(
	SEQ_CHALLENGE_COMMENT.nextval
	,#{userNo}
	,#{chalNo}
	,#{recommentTarget}
	,#{reply}
	,SYSDATE
	,'Y'
	)

mapper.update:
update CHALLENGE_COMMENT
set REPLY = #{reply}
	,STATUS = 'M'
where COMMENT_NO = {commentNo}

update CHALLENGE_COMMENT
set STATUS = 'D'
where COMMENT_NO = {commentNo}
